def _load_checkpoint(model, ckpt_path):
    if ckpt_path is not None:
        sd = torch.load(ckpt_path, map_location="cpu")["model"]
        missing_keys, unexpected_keys = model.load_state_dict(sd)
        if missing_keys:
            logging.error(missing_keys)
            raise RuntimeError()
        if unexpected_keys:
            logging.error(unexpected_keys)
            raise RuntimeError()
        logging.info("Loaded checkpoint sucessfully")

def build_sam2_with_finetuning(
    config_file,
    ckpt_path,
    finetuned_checkpoint=None,
    device="cuda",
    mode="eval",
    hydra_overrides_extra=[],
    apply_postprocessing=True,
):
    # Build base sam2 model using the original function
    hydra_overrides = [
        "++model._target_=sam2.sam2_video_predictor.SAM2VideoPredictor",
    ]
    if apply_postprocessing:
        hydra_overrides_extra = hydra_overrides_extra.copy()
        hydra_overrides_extra += [
            # dynamically fall back to multi-mask if the single mask is not stable
            "++model.sam_mask_decoder_extra_args.dynamic_multimask_via_stability=true",
            "++model.sam_mask_decoder_extra_args.dynamic_multimask_stability_delta=0.05",
            "++model.sam_mask_decoder_extra_args.dynamic_multimask_stability_thresh=0.98",
            # the sigmoid mask logits on interacted frames with clicks in the memory encoder so that the encoded masks are exactly as what users see from clicking
            "++model.binarize_mask_from_pts_for_mem_enc=true",
            # fill small holes in the low-res masks up to `fill_hole_area` (before resizing them to the original video resolution)
            "++model.fill_hole_area=8",
        ]
    hydra_overrides.extend(hydra_overrides_extra)

    # Read config and init model
    cfg = compose(config_name=config_file, overrides=hydra_overrides)
    OmegaConf.resolve(cfg)
    model = instantiate(cfg.model, _recursive_=True)
    if ckpt_path is not None:
        sd = torch.load(ckpt_path, map_location="cpu")["model"]
        missing_keys, unexpected_keys = model.load_state_dict(sd)
        if missing_keys:
            logging.error(missing_keys)
            raise RuntimeError()
        if unexpected_keys:
            logging.error(unexpected_keys)
            raise RuntimeError()
        logging.info("Loaded checkpoint sucessfully")

    # If there is a fine-tuned checkpoint, load it into the sam2 model
    if finetuned_checkpoint is not None:
        # Load the fine-tuned checkpoint
        finetuned_sd = torch.load(finetuned_checkpoint, map_location="cpu")["model"]

        finetuned_renamed = {}
        # Get the model's current state dict
        current_state_dict = model.state_dict()
        for k,v in finetuned_sd.items():
            k_new = k.replace("sam2_model.","")
            finetuned_renamed[k_new] = v

        # print("="*50)
        # for k in current_state_dict:
        #     print(k)

        # print("="*50)
        # for k,v in current_state_dict.items():
        #     if k in finetuned_renamed:
        #         print(k)
        # Filter out incompatible keys and load only matching layers
        finetuned_renamed = {k: v for k, v in finetuned_renamed.items() if k in current_state_dict}

        # Update the sam2_model's state dict with the filtered fine-tuned layers
        missing_keys, unexpected_keys = model.load_state_dict(finetuned_renamed, strict=False)

        # Debug: Print missing and unexpected keys for inspection
        if missing_keys:
            print(f"Keys missing after loading fine-tuned weights: {missing_keys}")
        if unexpected_keys:
            print(f"Unexpected keys after loading fine-tuned weights: {unexpected_keys}")

        logging.info("Fine-tuned checkpoint loaded successfully")
    model = model.to(device)
    if mode == "eval":
        model.eval()
    # Return the sam2 model (now with fine-tuned weights)
    return model


